/*

This will allow you to reload specific commands, all events, 
all commands, or both with *

reload [cmdname] - reload specific command
'reload *'' - reload everything
'reload commands' - reload all commands
'reload events' - reload all events

If you would like, please consider donating as
it allows me to create more things like this!
*/


const fs = require('fs');
const path = require('path');  

const commandName = tempVars("cmdname");

const filePath = path.join(process.cwd(), 'data', 'commands.json');
const filePath2 = path.join(process.cwd(), 'data', 'events.json');

(()=>{

   if(fs.existsSync(filePath2)){

      let content = fs.readFileSync(filePath2)  
      try {
         if(typeof content !== 'string' && content.toString) content = content.toString();
         Files.data.events = JSON.parse(Files.decrypt(content));
      } catch(e) {
         console.error(e)
         return;
      }
   }


   if(fs.existsSync(filePath)){

      let content = fs.readFileSync(filePath)  
      let data;
      try {
         if(typeof content !== 'string' && content.toString) content = content.toString();
         data = JSON.parse(Files.decrypt(content));
      } catch(e) {
         console.error(e)
         return;
      }

      if(commandName ===  undefined) return msg.channel.send(`
      **Command Reloader Error**    

      Please type either a partial name or a complete name of a command to reload that particular command.

      If you put '*' as the command name, it will reload ALL commands AND events from the data files.

      If you put 'commands' as the command name, it will reload ALL commands from the data files.

      If you put 'events' as the command name, it will reload ALL events from the data files.

      New command aliases will be added, old ones will not be removed
      `)

      function reloadEvents(){
         msg.channel.send(`Attempting to Reload ${Files.data.events.length} Events...`)

         for(const event of client.eventNames()) {  
            client.removeAllListeners(event);  
         }

         Bot.$evts = {}; 

         Bot.setupBot();
         Bot.reformatEvents();
         Bot.initEvents();

         if(Bot.$evts["3"]) {
            Events.setupIntervals(bot);
         }
      }

      function reloadCommands(){
         msg.channel.send(`Attempting to Reload ${data.length} Commands...`)

         Files.data.commands = data;

         Bot.$cmds = {}; 
         Bot.$icds = []; 
         Bot.$regx = []; 
         Bot.$anym = [];

         Bot.reformatCommands();
      }

      if(commandName === "*"){

         try {
            reloadCommands();
            reloadEvents();           
         } catch (error) {
            console.error(error)
            return msg.channel.send(`There was a problem reloading everything.`); 
         }

         return msg.channel.send(`All Bot Data Reloaded!`); 
      } 

      if(commandName === "events"){
         try {
            reloadEvents();
         } catch (error) {
            console.error(error)
            return msg.channel.send(`There was a problem reloading all events`); 
         }

         return msg.channel.send(`All ${Files.data.events.length} Events Reloaded!`); 
      }
      if(commandName === "commands"){

         try {
            reloadCommands();
         } catch (error) {
            console.error(error)
            return msg.channel.send(`There was a problem reloading all commands!`); 
         }

         return msg.channel.send(`All ${data.length} Commands Reloaded!`); 
      }



      let found = data.filter(c=>c && c.name.indexOf(commandName) == 0)

      if(found.length === 0) return msg.channel.send(`The command '${commandName}' was not found.`); 

      if(found.length > 1) return msg.channel.send(`${found.length} results, did you mean: ${found.map(c=>c.name).join(", ")}? Run command again.`)

         const com = data.find(c=>c && c.name === found[0].name)

         Bot._caseSensitive = Boolean(Files.data.settings.case === 'true');  
         if(com) {
            switch(com.comType) {
               case '1':
                  delete Bot.$icds.find(c=>c && c.name === found)
                  Bot.$icds.push(com);
                  break;
               case '2':
                  delete Bot.$regx.find(c=>c && c.name === found)
                  Bot.$regx.push(com);
                  break;
               case '3':
                  delete Bot.$anym.find(c=>c && c.name === found)
                  Bot.$anym.push(com);
                  break;
               default:
                  if(Bot._caseSensitive) {
                     Bot.$cmds[com.name] = com;
                     if(com._aliases) {
                        const aliases = com._aliases;
                        for(let j = 0; j < aliases.length; j++) {
                           Bot.$cmds[aliases[j]] = com;
                        }
                     }
                  } else {
                     Bot.$cmds[com.name.toLowerCase()] = com;
                     if(com._aliases) {
                        const aliases = com._aliases;
                        for(let j = 0; j < aliases.length; j++) {
                           Bot.$cmds[aliases[j].toLowerCase()] = com;
                        }
                     }
                  }
                  break;
            }

            msg.channel.send(`Command '${com.name}' Reloaded!`)
         }

   }
})();
